import React, { Component } from 'react'


import './admin.css';

import dataType from '../Data/dataTop'

let num = 0;

class Carddeux extends Component {
  constructor(props) {
    super(props); 
    this.state = {
      num: 0  //numéro profil par défaut
    }
  }

  render () {

    return (
      <div className='Carddeux'>
         <h3>Titre</h3>
        <br>
        </br>
        <img src={dataType[num].image}></img>
        <p>{dataType[num].desc}</p>
        <button className='btn btn-danger' onClick={this.change}>supprimer</button>
      </div>
    )
  }
}



class Formdeux extends Component {

  render () {

    return (
      <div>
          <h2>Types de bougies</h2>
          <br></br>
          <div className='container'>
            <div className='row'>
              <div className='col-md-12'>
                <form action="" method="POST">
                  <div class="form-row">
                    <div class="form-group col-md-6">
                      <label for="inputNom">Nom</label>
                      <input type="text" class="form-control" id="inputDate"/>
                    </div>
                    <div class="form-group col-md-6">
                      <label for="inputDescription">Description</label>
                      <input type="description" class="form-control" id="inputDescription"/>
                    </div>
                  </div>
                  <div class="form-row">
                    <div class="form-group col-md-6">
                      <label for="inputImage">Image</label>
                      <input type="test" class="form-control" id="inputImage"/>
                    </div>


                  </div>

                  <button type="submit" class="btn btn-primary">Ajouter</button>
                </form>
              </div>       
            </div>    
          </div>
          <Carddeux/>
        </div>
    )
  }
}

export default Formdeux
