import React, { Component } from 'react'


import './admin.css';

let numParf =0;

class Formulaire extends React.Component{


  render() {


    if(this.props.form==0){
      return (
        <div>
          <h2>Modifier stock de Vanille</h2>
          <br></br>
          <form>
            <label>
              Nouveau stock : 
            </label>
            <input type="text" name="bouteille" />   <br/>   
            <br/> 
            <input type="submit" value="Envoyer" />
          </form>
        </div>

      );  
    }
    else if(this.props.form==1){
      return (
        <div>
          <h2>Modifier stock de Fraise</h2>
          <br></br>
          <form>
            <label>
              Nouveau stock : 
            </label>
            <input type="text" name="bouteille" />   <br/>   
            <br/> 
            <input type="submit" value="Envoyer" />
          </form>
        </div>
      );
    }
    else if(this.props.form==2){
      return (
        <div>
          <h2>Modifier stock de Citron</h2>
          <br></br>
          <form>
            <label>
              Nouveau stock : 
            </label>
            <input type="text" name="bouteille" />   <br/>   
            <br/> 
            <input type="submit" value="Envoyer" />
          </form>
        </div>
      );
    }

    else if(this.props.form==3){
      return (
        <div>
          <h2>Modifier stock de Menthe</h2>
          <br></br>
          <form>
            <label>
              Nouveau stock : 
            </label>
            <input type="text" name="bouteille" />   <br/>   
            <br/> 
            <input type="submit" value="Envoyer" />
          </form>
        </div>
      );
    }

    else if(this.props.form==4){
      return (
        <div>
          <h2>Modifier stock de Chocolat</h2>
          <br></br>
          <form>
            <label>
              Nouveau stock : 
            </label>
            <input type="text" name="bouteille" />   <br/>   
            <br/> 
            <input type="submit" value="Envoyer" />
          </form>
        </div>
      );
    }

  }
}

class Formquatre extends Component {
  constructor(props) {
    super(props); 
    this.state = {
      numParf: 0  //numéro parfum par défaut
    }
  }

  changeParf0 = () => {
    numProf=0;
    this.setState({numProf: 0});
  }

  changeParf1 = () => {
    numProf=1;
    this.setState({numProf: 1});
  }

  changeParf2 = () => {
    numProf=2;
    this.setState({numProf: 2});
  }

  changeParf3 = () => {
    numProf=2;
    this.setState({numProf: 2});
  }

  changeParf4 = () => {
    numProf=2;
    this.setState({numProf: 2});
  }

  changeParf5 = () => {
    numProf=2;
    this.setState({numProf: 2});
  }



  render () {

    return (
      <center>
          <h1 className="fbtitle">Mini Facebook</h1>
          <br></br>
        <div className="container">
          <div className="row">
            <div className="col-sm-4"><button onClick={this.changeParf0}>Aurélien</button></div>
            <div className="col-sm-4"><button onClick={this.changeParf1}>Oscar</button></div>
            <div className="col-sm-4"><button onClick={this.changeParf2}> Julien</button></div>
          </div>
        </div>
        <br></br> <br></br> <br></br>
        <Formulaire form={numParf} />
        </center>  
    )
  }
}

export default Formquatre
